export function saveScrobbles(username, totalPages, fromTime) {
  let currentPage = totalPages;
  const promises = [];
  while (currentPage > 0) {
    const p = fetch(`/api/scrobbles/${username}`, {...});
    promises.push(p);
    currentPage -= 1;
  }
  return from(promises).pipe(mergeAll());
}

