import { useState } from "react"

export default function App({ name }) {
  const [count, setCount] = useState(0)
  return (
    <div className="App">
      <p>My name is {name}</p>
      <h1>Button Clicked: {count} times</h1>
      <button onClick={() => setCount((count) => count + 1)}>Add 1</button>
    </div>
  )
}
